// src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import StudentForm from './StudentForm';
import StudentList from './StudentList';
import './App.css';

function App() {
  const [students, setStudents] = useState([]);

  const baseUrl = "http://localhost:8080/api/student";

  useEffect(() => {
    getStudents();
  }, []);

  const getStudents = async () => {
    try {
      const headers = {
        'X-Time': new Date().toISOString(), // Example of adding time header
        'X-Browser': navigator.userAgent, // Example of adding browser header
        'X-Unique-ID': generateUniqueID() // Example of adding a unique identifier header
      };

      const response = await axios.get(baseUrl, { headers });
      setStudents(response.data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  const addStudent = async (student) => {
    try {
      const headers = {
        'X-Time': new Date().toISOString(), // Example of adding time header
        'X-Browser': navigator.userAgent, // Example of adding browser header
        'X-Unique-ID': generateUniqueID() // Example of adding a unique identifier header
      };

      await axios.post(baseUrl, student, { headers });
      getStudents(); // Refresh student list after adding a new student
    } catch (error) {
      console.error('Error adding student:', error);
    }
  };

  // Function to generate a unique ID (you can replace this with your own logic)
  const generateUniqueID = () => {
    return Math.random().toString(36).substr(2, 9); // Example of generating a random ID
  };

  return (
    <div className="App">
      <h1>Student Management</h1>
      <StudentForm addStudent={addStudent} />
      <StudentList students={students} />
    </div>
  );
}

export default App;
